package cn.buk.api.jiubaiyou.util;

import static org.junit.jupiter.api.Assertions.assertEquals;

import org.junit.jupiter.api.Test;

class DESUtilTest {

  @Test
  public void test() {
    final String secretKey = "12345678";
    final String encrypted = "";

    String temp = null;

    System.out.println(encrypted.length());
    try {
      temp = DESUtil.decryptDES(encrypted, secretKey);

      System.out.println(temp);

      String temp2 = DESUtil.encryptDES(temp, secretKey);
      System.out.println(temp2);

      assertEquals(encrypted, temp2);
    } catch (Exception e) {
      e.printStackTrace();
    }


  }
}